#!/usr/bin/python3
from typing import List

'''
https://leetcode.com/problems/3sum/
Given an integer array nums, return all the triplets [nums[i], nums[j], nums[k]] such that i != j, i != k, and j != k, and nums[i] + nums[j] + nums[k] == 0.
Notice that the solution set must not contain duplicate triplets.

Example:
Input: nums = [-1,0,1,2,-1,-4]
Output: [[-1,-1,2],[-1,0,1]]
'''
class Solution:

    def incrementTillUnique(self, i: int, nums: List[int]) -> int:
        while (i > 0 and i <= len(nums) - 1 and nums[i] == nums[i - 1]):
            i = i + 1
        return i

    def decrementTillUnique(self, i: int, nums: List[int]) -> int:
        while (i >= 0 and i < len(nums) - 1 and nums[i] == nums[i + 1]):
            i = i - 1
        return i

    # Find two values in the array starting from index "start_index" whose sum = target
    def twoSum(self, nums: List[int], start_index: int, target: int) -> List[List[int]]:
        result_list = []
        l = start_index
        r = len(nums) - 1
        while (l < r):
            if (nums[l] + nums[r] == target):
                result_list.append([-target, nums[l], nums[r]])
                l = self.incrementTillUnique(l+1, nums)
                r = self.decrementTillUnique(r-1, nums)
            elif(nums[l] + nums[r] < target):
                l = l + 1
            else:
                r = r - 1
        return result_list

    def threeSum(self, nums: List[int]) -> List[List[int]]:
        result_list = []
        nums.sort()
        i = 0
        while(i < len(nums) - 1):
            result = self.twoSum(nums, i+1, -nums[i])
            if len(result) > 0:
                for r in result:
                    result_list.append(r)
            i = self.incrementTillUnique(i+1, nums)
        return result_list
            


s = Solution()
